/** Accessor that connects with a browser on the local host.
 *  This is intended to be used by a swarmlet to interact with users,
 *  for example by providing forms to be filled in.
 *  For now, however, it simply displays HTML provided to its input.
 *
 *  @accessor utilities/Browser
 *  @input {string} html An HTML document to render in the browser.
 *  @input resources An object where each named property is an object containing
 *   two properties, 'data' and 'contentType'. The name of the named property is
 *   the path to be used to access the resource. The 'data' property is the resource
 *   itself, an arbitrary collection of bytes. The 'contentType' is the MIME
 *   type of the data.
 *  @author Edward A. Lee (eal@eecs.berkeley.edu)
 *  @version $$Id: Browser.js 1921 2017-07-03 17:09:27Z eal $$
 */

// Stop extra messages from jslint and jshint.  Note that there should be no
// space between the / and the * and global. See https://chess.eecs.berkeley.edu/ptexternal/wiki/Main/JSHint */
/*global  exports, require */
/*jshint globalstrict: true*/
"use strict";

var Browser = require('browser');
var browser = null;

exports.setup = function () {
    this.parameter('header', {
        'type': 'string',
        'value': ''
    });
    this.input('html', {
        'type': 'string'
    });
    this.input('resources');
    this.output('post', {
        'type': 'JSON'
    });
    this.parameter('port', {
        'type': 'number',
        'value': 8080
    });
};

var display = function () {
    // Check for any new resources.
    var resources = this.get('resources');
    if (resources) {
        for (var name in resources) {
            browser.addResource(name, resources[name].data, resources[name].contentType);
        }
    }

    var toDisplay = this.get('html');
    browser.display(toDisplay);
};


exports.initialize = function () {
    var self = this;

    browser = new Browser.Browser(
            {'port': self.getParameter('port')},
            self.getParameter('header')
    );
    browser.addListener('/', function(data) {
        self.send('post', JSON.parse(data));
    });

    this.addInputHandler('html', display.bind(this));

    this.addInputHandler('resources', function() {
        var resources = this.get('resources');
        for (var name in resources) {
            browser.addResource(name, resources[name].data, resources[name].contentType);
        }
    });
};

exports.wrapup = function () {
    if (browser) {
        browser.stop();
    }
};

/** Generate Vis-compatible Graph2D html from an input
 *
 *  @accessor JavaScriptGDPToGraph2D
 *  @input data The input data
 *  @output output The HTML page.
 *  @author Christopher Brooks
 *  @version $$Id: GDPLogRead.xml 75037 2016-08-11 21:29:55Z cxh $$
 */
exports.setup = function() {
    this.input('data');
    this.output('output', {'type':'string'});
};

function isNumeric(n) {
  return !isNaN(parseFloat(n)) && isFinite(n);
}
var items;

exports.generate = function() {
	var dataValue = this.get('data')
	// console.log("JavaScriptGDPToGraph2D: " + dataValue);
	items.push({'x': Date.now(), 'y': dataValue});
	var output = '<div id="visualization"></div>\n\
		<script type="text/javascript">\n\
			var container = document.getElementById(\'visualization\');\n\
      		var items = ' + JSON.stringify(items) + ';\n\
	        var dataset = new vis.DataSet(items);\n\
	        var options = {};\n\
	        var Graph2d = new vis.Graph2d(container, dataset, options);\n\
	    </script>\n';
	if (isNumeric(dataValue)) {
		this.send('output', output);
	}
}

exports.initialize = function() {
	items = [];
	handle = this.addInputHandler('data', this.exports.generate.bind(this));
}

