		};
		var height = y + padding;
		var svg = '<svg width="'  // Opening svg tag.
				+ width
				+ '" height="'
				+ height
				+ '">'
				+ '<rect width="' // Background rectangle.
				+ width
				+ '" height="'
				+ height
				+ '" style="fill:white;stroke:red;stroke-width:2;opacity:0.7"'
				+ '></rect>'
				+ '<text x="'     // Name column.
				+ padding
				+ '" y="'
				+ padding
				+ '">'
				+ nameColumn
				+ '</text>'
				+ '<text x="'     // Value column.
				+ (padding + nameColumnWidth)
				+ '" y="'
				+ padding
				+ '">'
				+ valueColumn
				+ '</text>'
				+ '</svg>';       // Closing.
		this.send('svg', svg);
	});
}
