/** This accessor annotates an image provided at the _image_ input
 *  by rendering an SVG graphic overlaid on the image.
 *
 *  The _graphic_ input, if provided, gives an SVG specification of the graphic.
 *  Alternatively, if a _graphic_ input is not provided, or the _graphic_ input
 *  is empty, the _graphicURI_ input can provide a resource identifier for the graphic.
 *  This can be a URL or a local resource (a file) available on the host.
 *  Note that most hosts will restrict locations from which files can be read.
 *  Usually they will allow to read files in the directory where the swarmlet is located
 *  on the file system, or in a subdirectory of that directory.
 *  To read such a file, give a path relative to the location of the swarmlet.
 *
 *  The _translate_
 *  input can be used to shift the graphic in the X and Y directions,
 *  and the _rotate_ input can used to rotate the graphic.
 *
 *  The _options_ input can have the following fields:
 *  * __XOffset__: The horizontal offset for the graphic. If this is specified
 *    and a _translate_ input is also provided, the both offsets are applied.
 *  * __YOffset__: The vertical offset for the graphic. If this is specified
 *    and a _translate_ input is also provided, the both offsets are applied.
 *
 *  @accessor image/ImageAnnotate
 *  @input image The image to annotate.
 *  @input graphic The location of an SVG graphic to overlay on the image.
 *  @input translate An offset to apply to the graphic, as two element array specifying
 *   horizontal and vertical offsets in pixels.
 *  @input options A JSON object specifying options.
 *  @output output The filtered image.
 *  @author Edward A. Lee
 *  @version $$Id$$
 */

// Stop extra messages from jslint.  Note that there should be no
// space between the / and the * and global.
/*globals addInputHandler, exports, get, getResource, input, output, removeInputHandler, require, send */
/*jshint globalstrict: true */
"use strict";

var imageFilters = require('imageFilters');

exports.setup = function () {
    this.input('image');
    this.input('graphic', {
        'value': '',
        'type': 'string'
    });
    this.input('graphicURI', {
        'value': '',
        'type': 'string'
    });
    this.input('scale', {
        'value': 1.0,
        'type': 'number'
    });
    this.input('rotation', {
        'value': 0.0,
        'type': 'number'
    });
    this.input('translate');
    this.input('options', {
        'value': '',
        'type': 'JSON'
    });
    this.output('output');
};

exports.initialize = function () {
    this.addInputHandler('image', function () {
        var image = this.get('image');
        var options = this.get('options');
        if (!options) {
            options = {};
        }

        var scale = this.get('scale');
        if (scale !== null) {
            // Combine with scale options, if specified.
            if (options.Scale) {
                scale *= options.Scale;
            }
            options.Scale = scale;
        }

        var rotation = this.get('rotation');
        if (rotation !== null) {
            // Combine with rotation options, if specified.
            if (options.Rotation) {
                rotation += options.Rotation;
            }
            options.Rotation = rotation;
        }

        var translate = this.get('translate');
        if (translate !== null && translate[0] !== null && translate[1] !== null) {
            // Combine with offset options, if specified.
            var xOffset = translate[0];
            if (options.XOffset) {
                xOffset += options.XOffset;
            }
            options.XOffset = xOffset;

            var yOffset = translate[1];
            if (options.YOffset) {
                yOffset += options.YOffset;
            }
            options.YOffset = yOffset;
        }
        var graphic = this.get('graphic');
        if (graphic) {
            options.Graphic = graphic;
        } else {
            var graphicURI = this.get('graphicURI');
            if (graphicURI) {
                // Second argument is a timeout.
                options.Graphic = this.getResource(graphicURI, 3000);
            }
        }
        var result = imageFilters.filter(image, 'Annotate', options);
        this.send('output', result);
    });
};

exports.setup = function() {
	this.input('reset');
	this.input('translation');
	this.input('json');
	this.output('svg', {'type':'string'});
	this.output('latestTranslation');
	this.parameter('padding', {'type':'number', 'value':10});
	this.parameter('rowPadding', {'type':'number', 'value':5});
	// Font size in pixels.
	this.parameter('fontSize', {'type':'number', 'value':20});
	// Average character width (hint for box width)
	this.parameter('averageCharacterWidth', {'type':'number', 'value':0.75});
}
exports.initialize = function() {
	var thiz = this;

	var fontSize = this.get('fontSize');
	// Hack to estimate the width of text to make the box:
	var averageCharacterWidth = this.get('averageCharacterWidth');
	var padding = this.getParameter('padding');
	var rowPadding = this.getParameter('rowPadding');
	// Default graphic.
	var noData = '<svg>'
		+ '<rect style="fill:white;stroke:red;stroke-width:2;opacity:0.7" width="'
		+ (7*fontSize*averageCharacterWidth)
		+ '" height="'
		+ (fontSize + 2*padding)
		+ '"/>'
		+ '<text x="'
		+ padding
		+ '" y="'
		+ (padding + fontSize)
		+ '" style="font-size:'
		+ fontSize 
		+ 'px">No data</text>'
		+ '</svg>';
	var graphic = noData;

	this.addInputHandler('reset', function() {
		if (thiz.get('reset')) {
			graphic = noData;
		}
	});
	this.addInputHandler('json', function() {
		var json = thiz.get('json');
		try {
			json = JSON.parse(json);
		} catch(err) {
			json = {'error': err.message};
		}
		if (!json || Object.keys(json).length === 0) {
			graphic = noData;
			return;
		}
		var nameColumn = '';
		var valueColumn = '';
		var y = padding + fontSize;
		var nameColumnWidth = 0;
		for (property in json) {
			nameColumn += '<tspan x="'
					+ padding
					+ '" y="'
					+ y
					+ '">'
					+ property
					+ '</tspan>';
			y += (rowPadding + fontSize);
			if (nameColumnWidth < property.length) {
				nameColumnWidth = property.length;
			}
		}
		y = padding + fontSize;
		var valueColumnWidth = 0;
		var x = (padding + nameColumnWidth * fontSize * averageCharacterWidth);
		for (property in json) {
			value = util.inspect(json[property]);
			valueColumn += '<tspan x="'
					+ x
					+ '" y="'
					+ y
					+ '">'
					+ value
					+ '</tspan>';
			y += (rowPadding + fontSize);
			if (valueColumnWidth < value.length) {
				valueColumnWidth = value.length;
			}
		};
		var height = y;
		var width = (nameColumnWidth + valueColumnWidth) 
				* fontSize * averageCharacterWidth
				+ (2 * padding);
		graphic = '<svg width="'  // Opening svg tag.
				+ width
				+ '" height="'
				+ height
				+ '">'
				+ '<rect width="' // Background rectangle.
				+ width
				+ '" height="'
				+ height
				+ '" style="fill:white;stroke:red;stroke-width:2;opacity:0.7"'
				+ '></rect>'
				+ '<text style="font-size:'   // Name column.
				+ fontSize 
				+ 'px" x="'     
				+ padding
				+ '" y="'
				+ padding
				+ '">'
				+ nameColumn
				+ '</text>'
				+ '<text style="font-size:'   // Value column.
				+ fontSize 
				+ 'px" x="'     
				+ padding
				+ '" y="'
				+ (padding + fontSize)
				+ '">'
				+ valueColumn
				+ '</text>'
				+ '</svg>';       // Closing.
	});
	this.addInputHandler('translation', function() {
		var translation = thiz.get('translation');
		thiz.send('latestTranslation',translation);
		thiz.send('svg', graphic);
	});
}

