/** Test accessor that spontaneously produces outputs once per time interval.
 *  This implementation produces a counting sequence.
 *
 *  @accessor test/TestSpontaneous
 *  @parameter interval The interval between outputs in milliseconds.
 *  @output output Output for the counting sequence, of type number.
 *  @author Edward A. Lee
 *  @version $$Id$$
 */

// Stop extra messages from jslint.  Note that there should be no
// space between the / and the * and global.
/*globals clearInterval, exports, require, setInterval */
/*jshint globalstrict: true*/
"use strict";

exports.setup = function () {
    this.parameter('interval', {
        'type': 'number',
        'value': 1000
    });
    this.output('output', {
        'type': 'number'
    });
};

// These variables will not be visible to subclasses.
var handle = null;
var count = 0;

exports.initialize = function () {
    count = 0;
    // Need to record 'this' for use in the callback.
    var thiz = this;
    handle = setInterval(function () {
        thiz.send('output', count);
        count += 1;
    }, this.getParameter('interval'));
};

exports.wrapup = function () {
    if (handle) {
        clearInterval(handle);
        handle = null;
    }
};

var httpClient = require('@accessors-modules/http-client');
	//Test
var IdToResource = {
	'127': 'PressureSensor.js',
	'435': 'VibrationSensor.js',
	'105': 'TemperatureSensor.js'	
};

exports.setup = function() {
	// Inputs
	this.input('tags');
	this.input('index', {
		'type':'int', 
		'value':0
	});

	// Outputs
	this.output('accessor', {
		'type': 'string',
		'value': ''
	});
	this.output('translation');
}

exports.initialize = function() {
	var thiz = this;
	var previousIndex = -1;

	this.addInputHandler('tags', function() {
		var tags = thiz.get('tags');
		var index = thiz.get('index');

		if (index != previousIndex) {
			// Index has changed. Look up an accessor and produce it.
			if (tags && tags.length > index) {
				var id = tags[index].id;
				// FIXME: Change getResource API.
				var accessor = getResource(IdToResource[id], 2000);
				if (accessor) {
					thiz.send('accessor', accessor);
				}
			}
			previousIndex = index;
		}
		thiz.send('translation', tags[index].center);
	});
}

// This script defines a MutableAccessor that can be reified
// by accessors having a control input and data and schema outputs.

exports.setup = function() {
	// First, set that this is a mutable accessor that
	// realizes 'controling' functionality
	this.mutable(true);	
	this.realize('controllableSensor');

	// Defining inputs
	// the MutableAccessor will receive in this input the acessor code
	this.input('accessor', {
		'type': 'string',
		'value': ''
	});
	// This input can be used by the reified accessor
	this.input('control', {
		'type': 'JSON',
	});

	// Defining outputs
	this.output('data', { 'spontaneous':true,
		'type': 'JSON',
		'value': null		
	});
	this.output('schema', { 'spontaneous':true,
		'type': 'JSON',
		'value': null
	});
};

exports.initialize = function() {
	var thiz = this;

	this.addInputHandler('accessor', function() {
		var accessorCode = this.get('accessor');
		thiz.send('data', null);
		if (accessorCode && accessorCode.length > 0) {
			thiz.reify(accessorCode);
		} else {
			thiz.unreify();
		}
	});
};

// Upon receiving the first non-empty array of tags at the input, this script
// outputs a 0 to specify to use the first tag. After it has received
// holdCount more inputs, it outputs a 1 to select the second tag, if there
// is one. It continues like this cycling back to 0 whenever there are not
// enough tags to select the next index.  This also outputs true on the
// reset output each time the index changes or if there are no tags.

exports.setup = function() {
	this.input('tags');
	this.output('reset', {
		'type': 'boolean'
	});
	this.output('index', {
		'type': 'int'
	});
	this.parameter('holdCount', {
		'type': 'int',
		'value': 10
	});
}

exports.initialize = function() {
	var thiz = this;

	var first = true;
	var index = 0;
	var count = 0;
	var holdCount = thiz.get('holdCount');

	this.addInputHandler('tags', function() {
		var tags = thiz.get('tags');
		if (first && tags && tags.length > 0) {
			thiz.send('index', index);
			first = false;
			thiz.send('reset', true);
			return;
		}
		count++;
		if (count >= holdCount) {
			count = 0;
			// Only switch if there is more than one tag.
			if (tags && tags.length > 1) {
				index++;
				if (index >= tags.length) {
					index = 0;
				}
				thiz.send('index', index);
				thiz.send('reset', true);
			}
		}
		if (!tags) {
			thiz.send('reset', true);
		}
	});
}

// First attempt based on: https://stackoverflow.com/questions/12905849/generating-html-forms-from-json-or-xml-feed-using-jquery
// A proper Form generator will require a DOM parser to insert things into an existing HTML source. This is just a one-off accessor.

exports.setup = function() {
	this.input('image');
	this.input('spec', {'type':'JSON'});
	this.output('html', {'type':'string'});
	this.output('resources');
}
exports.initialize = function() {
	var thiz = this;

	var htmlSent = false;
	var spec = null;

	this.addInputHandler('spec', function() {
		var newSpec = JSON.stringify(thiz.get('spec'));
		if (newSpec != spec) {
			// Force a new web page if a new form has to be generated.
			htmlSent = false;
			spec = newSpec;
		}
	});
	this.addInputHandler('image', function() {
		var image = this.get('image');
		this.send('resources', {
			'image.jpg': {
				'data': image,
				'contentType': 'image/jpeg'
			}
		});
		var html = '<h1>Augmented Reality Demo</h1>\n';

		if (spec) {
		html += '\
		<script type="text/javascript"> \n\
		var BrutusinForms = brutusin["json-forms"]; \n \
		var bf = null; \n \
		function submit() { \n \
	      var form = document.getElementById("container").getElementsByTagName("form")[0]; \n \
	      if (!bf.validate()) { \n \
	        alert("Invalid form data."); \n \
	      } else { \n \
	        form.submit();\n \
	      } \n \
	    } \n \
		BrutusinForms.postRender = function() {\n \
	      var form = document.getElementById("container").getElementsByTagName("form")[0]; \n \
	      form.setAttribute("method", "POST"); \n \
	      form.setAttribute("action", "")\n \
	      console.log("Post render")\n \
	    }\n\
		bf = BrutusinForms.create( \n';
		html += spec;
		html += '); \n\
	    var container = document.getElementById("container"); \n\
	    bf.render(container); \n\
	  </script> \n';
		}
		// For the image, we use a timed update to avoid the web page
		// flashing on reloading the whole thing.  The ?count=number
		// appended to the image file name forces the browser to reload
		// the image, but the server ignores this additional information
		// and just serves the latest version of the image.
		html += '<img src="image.jpg" id="image" width="600"/>\n \
      <script>var count = 0;\n \
      setInterval(function() {\n \
         var e = document.getElementById("image");\n \
         e.src = "image.jpg?count=" + (count++);\n \
      }, 1000);\n \
      </script>\n';
      if (spec) {
      	html += '\
      <div id="container"></div> \n\
	  <button class="btn btn-primary" onclick="submit()">Submit</button>\n';
		}
		if (!htmlSent) {
			thiz.send('html', html);
			htmlSent = true;
		}
	});
}

