/** Subscribe to a Global Data Plane (GDP) log.
 *
 *  FIXME: What is the meaning of the startrec and numrec arguments? Makes no sense for subscription.
 *
 *  @output {string} data The data that is read from the log.
 *
 *  @input {string} logname The GDP logname.  By convention, a log name should be
 *   a reverse fully qualified name like "org.terraswarm.accessor.demo.MyDemoName".
 *   If this input is an empty string, then no subscription will
 *   occur until a non-empty value is provided.
 *   By default, this is empty.
 *   Providing an empty string will unsubscribe from any previously subscribed-to log.
 *   See the [GDP Infrastructure wiki[(https://gdp.cs.berkeley.edu/redmine/projects/gdp-sensor-and-actuator-deployments/wiki/Berkeley_Sensor_Deployments)
 *   for a list of GDP logs.
 *  @input {string} logdname The name of a logd server.  If empty,
 *   then the hostname of the local machine is used. This server is
 *   a gateway into the GDP.
 *   FIXME: Shouldn't this be a parameter rather than input?
 *
 *  @input {int} numrec The number of records to read.
 *
 *  @input {int} startrec The record number to be read.  In the GDP,
 *  the first record is record 1.
 *
 *  @input {int} timeout The timeout in milliseconds.
 *
 *  @parameter {string} debugLevel The value of the GDP debug flag.
 *  See gdp/README-developers.md for a complete summary.  The value is
 *  typically "pattern=level", for example "gdplogd.physlog=39".  To
 *  see the patterns, use the "what" command or strings
 *  $PTII/lib/libgdp* | grep '@(#)'.  Use "*=40" to set the debug
 *  level to 40 for all components. The value of level is not usually
 *  over 127.  Values over 100 may modify the behavior.
 *
 *  @accessor gdp/GDPLogSubscribe
 *  @author Christopher Brooks, Edward A. Lee, Nitesh Mor
 *  @version $$Id$$
 */

// Stop extra messages from jslint.  Note that there should be no
// space between the / and the * and global.
/*globals addInputHandler, console, exports, getParameter, input, output, parameter, removeInputHandler, require, send */
/*jshint globalstrict: true */
"use strict";

var GDP = require('@accessors-modules/gdp');
var log = null;
var handle = null;
var oldLogname = null;

// If getNextData() returns null data, then set sawNull to true.
var sawNull = false;

/** Setup the parameters and ports. */
exports.setup = function () {
    this.output('data', {
        'type': 'string'
    });
    this.parameter('debugLevel', {
        'type': 'string'
    });
    this.input('logname', {
        'type': 'string',
        'value': ''
    });
    this.input('logdname', {
        'type': 'string',
        'value': 'edu.berkeley.eecs.gdp-01.gdplogd'
    });
    this.parameter('numrec', {
        'type': 'int',
        'value': 0
    });
    this.parameter('startrec', {
        'type': 'int',
        'value': 0
    });
    this.parameter('timeout', {
        'type': 'int',
        'value': 0
    });
};

/** Add an input handler that will subscribe to a log. */
exports.initialize = function () {
    var self = this;

    // Set an input handler to unsubscribe and then invoke this initialize()
    // function when a new logname is provided.
    this.addInputHandler('logname', function () {
        // If there is an open subscription, close it.
        self.exports.wrapup.call(self);
        // Open the new subscription.
        self.exports.subscribe.call(self);
    });
    self.exports.subscribe.call(self);
};

/** If a non-empty logname is given, subscribe to the log. */
exports.subscribe = function () {
    var self = this,
        logname = this.get('logname'),
        logdname = this.get('logdname');

    if (logname === '') {
        // Nothing more to do.
        // ptolemy/actor/lib/jjs/modules/gdp/test/auto/GDPLogSubscribe.xml and
        // ptolemy/actor/lib/jjs/modules/gdp/test/auto/GDPLogSubscribeJS.xml
        // both end up calling subscribe() with an empty log name
        // during start up.
        return;
    }

    // Create or connect to a log.
    // The second argument specifies to open the log "read only."
    log = new GDP.GDP(logname, 1, logdname);

    // Listen for data from the log.
    log.on('data', function (data) {
        // De-duplicate if the data is the same as the first data.
        if ((self.firstData === null) || !self.firstData.equals(data)) {
            console.log('****** received: ' + data);
            self.send('data', data);
            console.log('****** sent data: ' + data);
            // Avoid performing the comparison again.
            self.firstData = null;
        }
    });

    log.setDebugLevel(this.getParameter('debugLevel'));

    // Subscribe to the log so that 'data' events are emitted.
    log.subscribe(
        this.getParameter('startrec'),
        this.getParameter('numrec'),
        this.getParameter('timeout')
    );

    // Produce a first output that is the latest data.
    // Note that there is a race condition here because by the time the read
    // occurs, we may already have a 'data' event queued. This would result
    // in duplicated data. So we record the value of the first data, and
    // if the first event exactly matches it, then the first event is ignored.
    // Note that if there is nothing on the log, the log.read() yields a 404,
    // not found, error, so we surround with a try catch.
    self.firstData = null;
    try {
        self.firstData = log.read(-1);
        self.send('data', self.firstData);
    } catch(e) {
        // Ignore.
    }
};

/** Unsubscribe to the log. */
exports.wrapup = function () {
    if (log !== null) {
        log.unsubscribe();
        // The GDP close() method has a significant bug where calling
        // close() twice might abort or segfault.
        log = null;
    }
};

var httpClient = require('@accessors-modules/http-client');
	//Test
var IdToResource = {
	'127': "edu.berkeley.eecs.gdpaccessordemo.pressure.01",
	'435': "edu.berkeley.eecs.gdpaccessordemo.vibration.01",
	'105': "edu.berkeley.eecs.gdpaccessordemo.temperature.01"	
};

exports.setup = function() {
	// Inputs
	this.input('tags');
	this.input('index', {
		'type':'int', 
		'value':0
	});

	// Outputs
	this.output('logname', {
		'type': 'string',
		'value': ''
	});
	this.output('translation');
}

exports.initialize = function() {
	var thiz = this;
	var previousIndex = -1;

	this.addInputHandler('tags', function() {
		var tags = thiz.get('tags');
		var index = thiz.get('index');

		if (index != previousIndex) {
			// Index has changed. Look up an accessor and produce it.
			if (tags && tags.length > index) {
				var id = tags[index].id;
				// FIXME: Change getResource API.
				var logname = IdToResource[id];
				if (logname) {
					thiz.send('logname', logname);
				}
			}
			previousIndex = index;
		}
		thiz.send('translation', tags[index].center);
	});
}

