// Send to output ports using send(value, portName, channel).

exports.setup = function() {
	this.input('trigger');
    this.input('logname', {'type': 'string'}); 
    this.input('numrec', {'type': 'int'});
    this.input('startrec', {'type': 'int'});
    this.output('toSend', {'type': 'string'});
