exports.initialize = function() {
	var self = this;
	this.addInputHandler('received', function() {
		try { 
			var obj = JSON.parse(self.get('received')); 
        	if (obj.ep_stat[0] != 200) {
        		error(JSON.stringify(obj.ep_stat)); 
			}
        	this.send('_logname', obj.logname); 
        	if (obj.type == 1) { 
        		self.send('recno', parseInt(obj.datum.recno));
        		self.send('data', obj.datum.data);
