exports.setup = function() {
	this.input('reading');
	this.input('number');
	this.output('isEqual', {'type' : 'boolean'});

	// For some reason self.get('number') returns null in callback for 
	// this.addInputHandler('reading').  So, cache value using separate
	// input handler.
	this.number = null;
};

exports.initialize = function() {
	var self = this;

	this.addInputHandler('number', function() {
		self.number = self.get('number');
	});

	this.addInputHandler('reading', function() {
		var reading = self.get('reading');
		// Don't care if types match.  OK as long as values match.
		self.send('isEqual', reading == self.number);
	});
};

