property.value:
"use strict";

var socket = require('socket');
var crypto = require('crypto');
var iotAuth = require('iotAuth');
var msgType = iotAuth.msgType;
var dataConverter = require('dataConverter');

exports.setup = function () {
    this.input('toSend');
    this.output('listening', {
        type: 'int'
    });
    this.output('connection');
    this.output('received');

    this.parameter('authHost', {
        type : 'string',
        value : 'localhost'
    });
    this.parameter('authPort', {
        value: -1,
        type: 'int'
    });
    this.parameter('authCertPath', {
        value: '',
        type: 'string'
    });

    this.parameter('entityName', {
        value: '',
        type: 'string'
    });
    this.parameter('entityPrivateKeyPath', {
        value: '',
        type: 'string'
    });

    this.parameter('publicCipherAlgorithm', {
        value: '',
        type: 'string'
    });
    this.parameter('signAlgorithm', {
        value: '',
        type: 'string'
    });
    this.parameter('distCipherAlgorithm', {
        value: '',
        type: 'string'
    });
    this.parameter('distHashAlgorithm', {
        value: '',
        type: 'string'
    });

    this.parameter('serverPort', {
        type : 'int',
        value : 4000
    });
    this.parameter('sessionCipherAlgorithm', {
        value: '',
        type: 'string'
    });
    this.parameter('sessionHashAlgorithm', {
        value: '',
        type: 'string'
    });

    this.parameter('receiveType', {
        type : 'string',
        value : 'string',
        options : ['string', 'image', 'byteArray']
    });
    this.parameter('sendType', {
        type : 'string',
        value : 'string',
        options : ['string', 'image', 'byteArray']
    });
};

var SEQ_NUM_SIZE = 8;

/// crypto info
var sessionCipherAlgorithm;
var sessionHashAlgorithm;

// type info
var receiveType;
var sendType;

// Auth & entity keys
var authPublicKey;
var entityPrivateKey;

// local variables
var self;
var currentDistributionKey = null;
var server = null;

var currentSessionKey = null;
var currentSecureServer = null;

function outputError(errorMessage) {
    console.log(errorMessage);
    self.send('error', errorMessage);
};

/*
callbackParams = {
    keyId,
    sendHandshake2Callback,
    handshake1Payload,
    serverSocket
}
*/
function sessionKeyRespCallback(status, distributionKey, sessionKeyList, callbackParams) {
    if (status.error) {
        console.log(status.error);
        console.log('session key request failed...');
        return;
    }
    console.log('session key request succeeded');

    if (distributionKey) {
        console.log('Updating to a new distribution key key');
        currentDistributionKey = distributionKey;
        console.log(currentDistributionKey);
    }

    console.log('received ' + sessionKeyList.length + ' session keys');
    var receivedSessionKey;
    for (var i = 0; i < sessionKeyList.length; i++) {
        receivedSessionKey = sessionKeyList[i]
        //currentSessionKeyList.push(sessionKeyList[i]);
        //outputSessionKey(receivedSessionKey);
        console.log(receivedSessionKey);
    }

    console.log('Session key arrived');

    receivedSessionKey.absValidity = new Date(receivedSessionKey.absValidity);

    if (receivedSessionKey.id == callbackParams.keyId) {
        console.log('Session key id is as expected');
        currentSessionKey = receivedSessionKey;
        console.log('switching to HANDSHAKE_1_RECEIVED state.');
        //entityServerState = entityServerCommState.HANDSHAKE_1_RECEIVED;
        callbackParams.sendHandshake2Callback(callbackParams.handshake1Payload,
            callbackParams.serverSocket, currentSessionKey);
    }
    else {
        outputError('Session key id is NOT as expected');
    }
};

function entityServerOnListening(listeningPort) {
    console.log('Server: Listening for socket connection requests on port ' + listeningPort);
    self.send('listening', listeningPort);
};
function entityServerOnError(message) {
    outputError('Error in server - details: ' + message);
};

function entityServerSocketOnClose(socketID) {
    console.log('secure connection with the client closed.');
    self.send('connection', socketID);
};
function entityServerSocketOnError(message) {
    outputError('Error in secure server - details: ' + message);
};
function entityServerSocketOnConnection(socketID) {
    console.log('secure connection with the client established.');
    self.send('connection', socketID);
};

function entityServerSocketOnData(data) {
    console.log('data received from server via secure communication');

    if (receiveType == 'string') {
    	self.send('received', data.toString());
	}
    else if (receiveType == 'image') {
    	self.send('received', dataConverter.jsArrayToImage(data.getArray()));
    }
    else if (receiveType == 'byteArray') {
    	self.send('received', data.getArray());
    }
    //self.send('receivedID', connectionCount);
}

function secureConnectionCallback(status, entityServerSocket) {
    if (status.error) {
        outputError(status.error);
        console.log('communication initialization failed...');
        return;
    }
    console.log('communication initialization succeeded');
    currentSecureServer = entityServerSocket;
    //self.send('connection', true);

}

function sessionKeyGetter(handshake1Payload, serverSocket, sendHandshake2Callback) {
    var keyId = handshake1Payload.readUIntBE(0, iotAuth.SESSION_KEY_ID_SIZE);
    if (currentSessionKey != null && currentSessionKey.id == keyId) {
        sendHandshake2Callback(handshake1Payload, serverSocket, currentSessionKey);
    }
    else {
        console.log('session key NOT found! sending session key id to AuthService');
        //entityServerState = entityServerCommState.HANDSHAKE_1_RECEIVED;

        var options = {
            authHost: self.getParameter('authHost'),
            authPort: self.getParameter('authPort'),
            entityName: self.getParameter('entityName'),
            numKeys: 1,
            purpose: {keyId: keyId},
            distributionKey: currentDistributionKey,
            distCipherAlgorithm: self.getParameter('distCipherAlgorithm'),
            distHashAlgorithm: self.getParameter('distHashAlgorithm'),
            publicCipherAlgorithm: self.getParameter('publicCipherAlgorithm'),
            signAlgorithm: self.getParameter('signAlgorithm'),
            authPublicKey: authPublicKey,
            entityPrivateKey: entityPrivateKey
        };
        var callbackParams = {
            keyId: keyId,
            sendHandshake2Callback: sendHandshake2Callback,
            handshake1Payload: handshake1Payload,
            serverSocket: serverSocket
        }
        iotAuth.sendSessionKeyReq(options, sessionKeyRespCallback, callbackParams);
        //self.send('keyId', keyId);
    }
};

exports.toSendInputHandler = function () {
    var toSend = this.get('toSend');

    if (sendType == 'image') {
    	toSend = dataConverter.imageToJSArray(toSend);
    }
    // May be receiving inputs before client has been set.
    if (currentSecureServer) {
    	if (!currentSecureServer.checkSessionKeyValidity()) {
    		outputError('session key expired!');
    	}
    	else if (!currentSecureServer.send(toSend)) {
    		outputError('Error in sending data');
    	}
    }
    else {
        console.log('Discarding data because socket is not open.');
    }
};

exports.initialize = function () {
    //entityServerState = entityServerCommState.IDLE;
    currentSessionKey = null;

    authPublicKey = crypto.loadPublicKey(this.getParameter('authCertPath'));
    entityPrivateKey = crypto.loadPrivateKey(this.getParameter('entityPrivateKeyPath'));

    sessionCipherAlgorithm = this.getParameter('sessionCipherAlgorithm');
    sessionHashAlgorithm = this.getParameter('sessionHashAlgorithm');

    receiveType = this.getParameter('receiveType');
    sendType = this.getParameter('sendType');

    self = this;
    var options = {
        serverPort: this.getParameter('serverPort'),
        sessionCipherAlgorithm: sessionCipherAlgorithm,
        sessionHashAlgorithm: sessionHashAlgorithm
    };
    var eventHandlers = {
        onServerError: entityServerOnError,
        onServerListening: entityServerOnListening,

        onData: entityServerSocketOnData,
        onClose: entityServerSocketOnClose,
        onError: entityServerSocketOnError,
        onConnection: entityServerSocketOnConnection
    };
    server = iotAuth.initializeSecureServer(options, sessionKeyGetter, secureConnectionCallback, eventHandlers);

    this.addInputHandler('toSend', exports.toSendInputHandler.bind(this));
};
/** Close all sockets, unregister event listeners, and stop the server.
 */
exports.wrapup = function() {
//    sockets = [];

    if (server !== null) {
        server.stop();
        server = null;
    }
};
