property.value:
exports.setup = function() {
	this.input('requestOutput');
	this.input('input');
	this.input('pause');
	this.output('triggerOutput');
	this.output('full', {'spontaneous':true});
	this.output('population', {'type':'int'});
	this.parameter('capacity', {'type':'int', 'value':5});
	this.parameter('minimumSpacing', {'type':'number', 'value':1.0});
	this.parameter('speed', {'type':'number', 'value':1.0});
}

exports.initialize = function() {
	var thiz = this;
	var population = 0;
	var paused = false;
	var pendingOutput = 0;

	this.addInputHandler('input', function() {
		population++;
		thiz.send('population', population);
		var capacity = thiz.getParameter('capacity');
		if (population >= capacity) {
			if (population > capacity) {
				throw('Capacity of ' + capacity + ' has been exceeded!');
			}
			// Delay this output so that cycles work without a MicrostepDelay.
			setTimeout(function() {
				thiz.send('full', true);
			}, 0);
		}
	});

	function triggerOutput() {
		// Just in case, also check for a current pause input.
		var pauseRequest = thiz.get('pause');
		if (pauseRequest === true) {
			paused = true;
		} else if (pauseRequest === false) {
			paused = false;
		}
		if (!paused && pendingOutput > 0) {
			thiz.send('triggerOutput', true);
			pendingOutput--;
			population--;
			thiz.send('population', population);
			var capacity = thiz.getParameter('capacity');
			if (population < capacity) {
				thiz.send('full', false);
			}
			if (pendingOutput > 0) {
				// After a delay that maintains the spacing, trigger another
				// output.
				var minimumSpacing = thiz.getParameter('minimumSpacing');
				var speed = thiz.getParameter('speed');
				setTimeout(function() {
					triggerOutput.call(thiz);
				}, minimumSpacing/speed);
			}
		}
	}
	this.addInputHandler('pause', triggerOutput.bind(this));

	this.addInputHandler('requestOutput', function() {
		if (!paused) {
			thiz.send('triggerOutput', true);
			population--;
			thiz.send('population', population);
			var capacity = thiz.getParameter('capacity');
			if (population < capacity) {
				thiz.send('full', false);
			}
		} else {
			pendingOutput++;
		}
	});
}

exports.setup = function() {
	this.input('requestOutput');
	this.input('input');
	this.input('pause');
	this.output('triggerOutput');
	this.output('population', {'type':'int'});
	this.parameter('timeBetweenDeployments', {'type':'number', 'value':1.0});
	this.parameter('vehicles', {'type':'int', 'value':4});
}

exports.initialize = function() {
	var thiz = this;
	var population = thiz.getParameter('vehicles');
	var paused = false;
	var pendingOutput = 0;

	this.addInputHandler('input', function() {
		population++;
		thiz.send('population', population);
	});

	function triggerOutput() {
		// Just in case, also check for a current pause input.
		var pauseRequest = thiz.get('pause');
		if (pauseRequest === true) {
			paused = true;
		} else if (pauseRequest === false) {
			paused = false;
		}
		if (!paused && pendingOutput > 0) {
			thiz.send('triggerOutput', true);
			population--;
			thiz.send('population', population);
			pendingOutput--;
			if (pendingOutput > 0) {
				// After a delay that maintains the spacing, trigger another
				// output.
				var time = thiz.getParameter('timeBetweenDeployments');
				setTimeout(function() {
					triggerOutput.call(thiz);
				}, time);
			}
		}
	}
	this.addInputHandler('pause', triggerOutput.bind(this));

	this.addInputHandler('requestOutput', function() {
		if (!paused) {
			thiz.send('triggerOutput', true);
			population--;
			thiz.send('population', population);
		} else {
			pendingOutput++;
		}
	});
}
